import React, { useState } from "react";
import { BMI, PText, DivText, InputText, CalculatorContainer, SingleFormDiv } from "./Calculators.css";
import { CountingKcalContainer } from "../../components/CountingKcalComp/CountingKcalComp.css";
import Form from "../../components/AllAboutForm/Form/Form";
import Input from "../../components/AllAboutForm/Input/Input";

const Calculators = () => {

    const [bmiResult, setBmiResults] = useState(Number);
    const [whrResult, setWhrResult] = useState(Number);
    const [whtrResult, setWhtrResult] = useState(Number);
    const [nmcResult, setNmcResult] = useState(Number);
    const [lbmResult, setLbmResult] = useState(Number);
    const [lgResult, setLgResult] = useState(Number);

    const bmi = (data) => { 
        return setBmiResults(data.weight / (data.height * data.height));
    }

    const whr = (data) => {
        return setWhrResult(data.talia / data.biodra);
    }

    const whtr = (data) => {
        return setWhtrResult(data.talia / data.wzrost )
    }


    return (
        <>
            <DivText>
                Kalkulator Body Index Mass(BMI)
            </DivText>
            <CalculatorContainer>
                <SingleFormDiv>
                    <Form onSubmit={bmi} btnText="Sprawdź BMI">
                        <Input name="weight" label="Waga" placeholder="Waga" required={true} />
                        <Input name="height" label="Wysokość" placeholder="Wzrost np: 1.84" required={true} />
                    </Form>
                    Twoje BMI: {Math.round(bmiResult)}
                </SingleFormDiv>
                <SingleFormDiv>
                    <Form onSubmit={whr} btnText="Sprawdź BMI">
                        <Input name="talia" label="Waga" placeholder="Waga" required={true} />
                        <Input name="biodra" label="Wysokość" placeholder="Wzrost np: 1.84" required={true} />
                    </Form>
                    WHR = {Math.round(whrResult)}
                </SingleFormDiv>
                <SingleFormDiv>
                    <Form onSubmit={bmi} btnText="Sprawdź BMI">
                        <Input name="weight" label="Waga" placeholder="Waga" required={true} />
                        <Input name="height" label="Wysokość" placeholder="Wzrost np: 1.84" required={true} />
                    </Form>
                </SingleFormDiv>
            </CalculatorContainer>
            <DivText>

            </DivText>  
        </>
    )

}

export default Calculators;
