import styled from "styled-components";
import Form from "../../components/AllAboutForm/Form/Form";


export const BMI = styled(Form)`
    margin: 0 auto;
    width: 50%;
    border: 1px solid black;
    border-radius: 20px;
    padding: 30px;
    @media(max-width: 767px){
        width: 80%;
    }
    @media(min-width: 768px) and (max-width: 1199px){
        width: 80%;
    }
`
