import React, { useState } from "react";
import Input from "../../components/AllAboutForm/Input/Input";
import { BMI } from "./Calculators.css";
import { PText, } from "./Home.css";

const Calculators = () => {

    const [bmiResult, setBmiResults] = useState(Number);

    const bmi = (height, weight) => {
        return weight / (height * height);
    }

    const handleSubmit = (data) => {
        setBmiResults(bmi(data.height, data.weight));

    }

    return (
        <>
            <BMI onSubmit={handleSubmit} btnText="Sprawdź BMI">
                <Input name="weight" label="Waga" placeholder="wpisz swoją wagę" required={true} />
                <Input name="height" label="Wysokość" placeholder="wpisz swój wzrost np: 1.84" required={true} />
            </BMI>
            <div>
                Twoje BMI: {Math.round(bmiResult)}
            </div>
            <h1>Zakresy wartości BMI:</h1>
            <PText>mniej niż 16 - wygłodzenie</PText>
            <PText>16 - 16.99 - wychudzenie</PText>
            <PText>17 - 18.49 - niedowaga</PText>
            <p>18.5 - 24.99 - wartość prawidłowa</p>
            <p>25 - 29.99 - nadwaga</p>
            <p>30 - 34.99 - I stopień otyłości</p>
            <p>35 - 39.99 - II stopień otyłości</p>
            powyżej 40 - otyłość skrajna
        </>
    )
}

export default Calculators;
