import React, { useState } from "react";
import { BMI, PText, DivText, InputText } from "./Calculators.css";
import { CountingKcalContainer } from "../../components/CountingKcalComp/CountingKcalComp.css";

const Calculators = () => {

    const [bmiResult, setBmiResults] = useState(Number);

    const bmi = (height, weight) => {
        return weight / (height * height);
    }

    const handleSubmit = (data) => {
        setBmiResults(bmi(data.height, data.weight));

    }

    return (
        <>
            <DivText>
                Kalkulator Body Index Mass(BMI)


            </DivText>
            <CountingKcalContainer>
                <BMI onSubmit={handleSubmit} btnText="Sprawdź BMI">
                    <InputText name="weight" label="Waga" placeholder="Waga" required={true} />
                    <InputText name="height" label="Wysokość" placeholder="Wzrost np: 1.84" required={true} />
                </BMI>
            </CountingKcalContainer>
            <DivText>
                Twoje BMI: {Math.round(bmiResult)}
            </DivText>
        </>
    )

}

export default Calculators;
