import styled from "styled-components";

export const HomeContainer = styled.div`
    width: 70%;
    display: flex;
    flex-direction: column;
    margin: 0 auto;
`

export const DivSingleContainer = styled.div`
    display: flex;
    margin: 150px auto;
    width: 100%;
    justify-content: center;
    align-items: center;
    @media(max-width: 767px){
        margin:50px auto;
    }
`

export const InsideDivSingleContainer = styled.div`
    flex: 1;
`

export const H1Text = styled.h1`
    font-size: 70px;
    text-align: left;
    color: #1E8BF0;
    width: 70%;
    @media(max-width: 767px){
        font-size: 25px;
        width:100%;
        margin:0 0;
    }
`

export const H2Text = styled.h2`
    font-size: 20px;
    color: gray;
    width: 80%;
    @media(max-width: 767px){
        font-size: 12px;
        width:100%;
        margin:20px 0;
    }

`

export const Img = styled.img`
    width: 350px;
    height: 350px;
    object-fit: cover;
    border-radius: 50%;
    @media(max-width: 767px){
        width: 100px;
        height: 100px;
    }
`
