import React, { useState } from "react";
import { BMI, PText, DivText, InputText, CalculatorContainer } from "./Calculators.css";
import { CountingKcalContainer } from "../../components/CountingKcalComp/CountingKcalComp.css";
import Form from "../../components/AllAboutForm/Form/Form";
import Input from "../../components/AllAboutForm/Input/Input";

const Calculators = () => {

    const [bmiResult, setBmiResults] = useState(Number);

    const bmi = (height, weight) => {
        return weight / (height * height);
    }

    const handleSubmit = (data) => {
        setBmiResults(bmi(data.height, data.weight));

    }

    return (
        <>
            <DivText>
                Kalkulator Body Index Mass(BMI)


            </DivText>
            <CalculatorContainer>
                <Form onSubmit={handleSubmit} btnText="Sprawdź BMI">
                    <Input name="weight" label="Waga" placeholder="Waga" required={true} />
                    <InputText name="height" label="Wysokość" placeholder="Wzrost np: 1.84" required={true} />
                </Form>
            </CalculatorContainer>
            <DivText>
                Twoje BMI: {Math.round(bmiResult)}
            </DivText>
        </>
    )

}

export default Calculators;
