import React, { useState } from "react";
import { BMI, PText, DivText, InputText, CalculatorContainer, SingleFormDiv } from "./Calculators.css";
import { CountingKcalContainer } from "../../components/CountingKcalComp/CountingKcalComp.css";
import Form from "../../components/AllAboutForm/Form/Form";
import Input from "../../components/AllAboutForm/Input/Input";
import { bmrData } from "../../containers/BMRCounting/BMRCountingData";
import Dropdown from "../../components/AllAboutForm/Dropdown/Dropdown";
import { NavItem } from "react-bootstrap";

const Calculators = () => {

    const [bmiResult, setBmiResults] = useState(Number);
    const [whrResult, setWhrResult] = useState(Number);
    const [whtrResult, setWhtrResult] = useState(Number);
    const [nmcResult, setNmcResult] = useState(Number);
    const [lgResult, setLgResult] = useState(Number);

    const bmi = (data) => {
        return setBmiResults(data.weight / (data.height * data.height));
    }

    const whr = (data) => {
        return setWhrResult(data.talia / data.biodra);
    }

    const whtr = (data) => {
        console.log(data);
        return setWhtrResult((data.talia / data.wzrost) * 100)
    }

    const nmc = (data) => {
        data.sex === "Kobieta" ?
            setNmcResult(data.height - 100 - ((data.height - 150) / 2))
            :
            setNmcResult(data.height - 100 - ((data.height - 150) / 4))
    }


    const lg = (data) => {
        return setLgResult((data.węgle * data.indeks) / 100)
    }


    return (
        <>
            <DivText>
                Kalkulator Body Index Mass(BMI)
            </DivText>
            <CalculatorContainer>
                <SingleFormDiv>
                    <Form onSubmit={bmi} btnText="Sprawdź BMI">
                        <Input name="weight" label="Waga" placeholder="Waga" required={true} />
                        <Input name="height" label="Wysokość" placeholder="Wzrost np: 1.84" required={true} />
                    </Form>
                    {bmiResult !== 0 ? Math.round(bmiResult) : null}
                </SingleFormDiv>
                <SingleFormDiv>
                    <Form onSubmit={whr} btnText="Sprawdź WHR">
                        <Input name="talia" label="Talia" placeholder="Talia" required={true} />
                        <Input name="biodra" label="Biodra" placeholder="Wzrost np: 1.84" required={true} />
                    </Form>
                    WHR = {whrResult.toFixed(2)}
                </SingleFormDiv>
                <SingleFormDiv>
                    <Form onSubmit={whtr} btnText="Sprawdź WHTR">
                        <Input name="talia" label="Talia" placeholder="Wpisz obwód talii" required={true} />
                        <Input name="wzrost" label="Wzrost" placeholder="Wzrost np: 1.84" required={true} />
                    </Form>
                    WHTR = {whtrResult.toFixed(2)}
                </SingleFormDiv>
                <SingleFormDiv>
                    <Form onSubmit={nmc} btnText="Sprawdź należną masę ciała wzór Broka">
                        <Dropdown options={bmrData[0].options} name="sex" label="Płeć" />
                        <Input name="height" label="Wzrost" placeholder="Wpisz obwód talii" required={true} />
                    </Form>
                    NMC = {nmcResult.toFixed(2)}
                </SingleFormDiv>
                <SingleFormDiv>
                    <Form onSubmit={lg} btnText="Sprawdź BMI">
                        <Input name="węgle" label="Węglowodany" placeholder="Wpisz obwód talii" required={true} />
                        <Input name="indeks" label="Indeks Glikemiczny" placeholder="Wzrost np: 1.84" required={true} />
                    </Form>
                    LG = {lgResult.toFixed(2)}
                </SingleFormDiv>
            </CalculatorContainer>
            <DivText>

            </DivText>
        </>
    )

}

export default Calculators;
